import React from 'react';
import {useState} from 'react';
import Axios from 'axios';

import './css/main.css';
import './css/general.css';

const TestContainer = (props) => {
    const altText = "Galeriipilt " + props.id;
    return(
        <div key={props.key}>
            <img src={props.img} alt={altText}/>
        </div>
    );
};

const Main = () => {

    const [imageList, setImageList] = useState([]);

    const getImages = () => {
        fetch('http://localhost:3001/getimages')
        .then(res => res.json())
        .then(json => setImageList(json));
    };


    return(
        <section className="main">
            <section className="introduction">
                <h1>Mait Jurask</h1>
                <h1>PORTFOLIO</h1>
            </section>
            <section className="TestContainer">
                {imageList.map((obj) =>{
                    const {img, key} = obj;
                    const imgWithDir = "galeriipildid/" + img;
                    return(
                        <TestContainer id={key} img={imgWithDir}></TestContainer>
                    );
                })}
            </section>
            <section className="about">
                <img src={"./logo_white.png"} alt="Signature placeholder"/>
                <button type="button" onClick={getImages}>Contact Me</button>
            </section>
        </section>
    );
};

export default Main;
